import React, {useState} from 'react'
import useEth from "../contexts/EthContext/useEth";

const RequestDelivery = () => {
  const { state: { contract, accounts } } = useEth();

  const [to, setTo] = useState('')
  const [from, setFrom] = useState('')
  const [item, setItem] = useState('')
  const [value, setValue] = useState(0)
  let allegeEvent = null;
  const [requests, setRequests] = useState([])

  const handleInput = e => {
    switch(e.target.name) {
        case 'to': 
            setTo(e.target.value);
            break;
        case 'from':
            setFrom(e.target.value);
            break;
        case 'item': 
            setItem(e.target.value);
            break;
        case 'value': 
            setValue(e.target.value);
            break;
        default: 
            alert('Invalid input source.')
            break;
    }
  }

  const handleSubmit = async () => {
    // Validation: 
    if (to === '' || from === '' || item === '' || value === 0) {
        alert("Please fill in all the values.")
        return;
    }

    // Calling Contract if validation succeeds: 
    await contract.methods.requestDelivery(to, from, item).send({from: accounts[0], value: value}).then((response) => {
      console.log(response)
      let tempArray = requests
      tempArray.push(response.events.RequestEvent.returnValues.requestId)
      setRequests(tempArray)
    })
  }

  window.handleConfirm = async (requestId) => {
    console.log(requestId)
    await contract.methods.confirm(requestId).send({from: accounts[0]})
  }

  const handleAllege = (log) => {
    let requestId = log.returnValues.requestId.toString()
    if (requests.includes(requestId)) {
      document.getElementById('wrapper').insertAdjacentHTML('beforeend', 
                                                            '<p>Requesting delivery confirmation of requestId ' + requestId + '</p>' + 
                                                            '<button onClick="window.handleConfirm('+ requestId.toString() +')">Confirm</button>')
    }
  }

  if (contract != null && allegeEvent == null) {
    allegeEvent = contract.events.AllegeEvent()
    .on("data", function(log) {
      handleAllege(log);
    })
  }

  return (
    <div id='wrapper'>
        <h1>Request a Delivery</h1>
        <label htmlFor="to">Delivery Destination: </label>
        <input type="text" name='to' onChange={handleInput}/>

        <label htmlFor="from">Pickup From: </label>
        <input type="text" name='from' onChange={handleInput}/>

        <label htmlFor="item">Pick this up: </label>
        <input type="text" name='item' onChange={handleInput} />

        <label htmlFor="value">Attach payment(in wei): </label>
        <input type="number" name='value' onChange={handleInput} />

        <button onClick={handleSubmit}>Submit</button>
    </div>
  )
}

export default RequestDelivery
