import { EthProvider } from "./contexts/EthContext";
import "./App.css";
import AcceptDelivery from "./components/AcceptDelivery";
import RequestDelivery from "./components/RequestDelivery";
import ProclaimDelivery from "./components/ProclaimDelivery";


function App() {
  const domain = 'http://localhost:3000'
  const setLocation  = window.location.href;
  const request = setLocation.slice(domain.length, setLocation.length).toLowerCase()

  function getComponent(request) {
    if (request === '/requestdelivery') {
      return <RequestDelivery />
    }
    if (request === '/acceptdelivery') {
      return <AcceptDelivery />
    }
    if (request === '/proclaimdelivery') {
      return <ProclaimDelivery />
    }
  }

  const sendRequest = () => {
    fetch('http://localhost:3000/deliveries', {method: 'get'}).then(response=>response.json()).then(res => console.log(res))
    console.log('here')
  }

  return (
      <EthProvider>
        <div id="App" >
          <div className="container">
            <h1>Welcome to Decentralized Deliveries</h1>
              {getComponent(request)}
              <button onClick={sendRequest}>request</button>
          </div>
        </div>
      </EthProvider>
  );
}



export default App;
