import React, {useState} from 'react'
import { EthProvider } from "./contexts/EthContext";


const RequestDelivery = () => {
  const { state: { contract, accounts } } = useEth();
  const [to, setTo] = useState('')
  const [from, setFrom] = useState('')
  const [item, setItem] = useState('')
  const [value, setValue] = useState(0)

  const handleInput = (e) => {
    switch(e.target.name) {
        case 'to': 
            setTo(e.target.value);
            break;
        case 'from':
            setFrom(e.target.value);
            break;
        case 'item': 
            setItem(e.target.value);
            break;
        case 'value': 
            setValue(e.target.value);
            break;
        default: 
            alert('Invalid input source.')
            break;
    }
  }

  const handleSubmit = async () => {
    // Validation: 
    if (to == '' || from == '' || item == '' || value == 0) {
        alert("Please fill in all the values.")
        return;
    }

    // Calling Contract if validation succeeds: 
    await contract.methods.requestDelivery(to, from, item, {from: accounts[0], value: value})
  }

  return (
    <div>
        <h1>Request a Delivery</h1>
        <label htmlFor="to">Delivery Destination: </label>
        <input type="text" name='to' onChange={handleInput()}/>

        <label htmlFor="from">Pickup From: </label>
        <input type="text" name='to' onChange={handleInput()}/>

        <label htmlFor="item">Pick this up: </label>
        <input type="text" name='item' onChange={handleInput()} />

        <label htmlFor="value">Attach payment(in wei): </label>
        <input type="number" name='value' onChange={handleInput()} />

        <button onClick={handleSubmit()}>Submit</button>
    </div>
  )
}

export default RequestDelivery
