import React, {useRef} from 'react'
import useEth from "../contexts/EthContext/useEth";
import Axios from 'axios'

const serverUrl = 'http://localhost:3000/deliveries/'

const AcceptDelivery = () => {
  
    const { state: { contract, accounts } } = useEth()
    const deliveryMan = accounts[0]

    if(deliveryMan != null) {
      Axios.get(serverUrl+'deliveryman/'+deliveryMan+'/stage/0').then(response => {
        console.log(response)
      })
    }

    const deliveryHash = useRef('');

    const acceptDelivery = async () => {
      const hash = deliveryHash.current.value;
      if(hash === '') {
        alert('Please enter the delivery key');
        return;
      }
      try {
        await contract.methods.acceptDelivery(hash).send({from: accounts[0]}).then((response) => {
          console.log(response)
        })
      } catch(err) {
        alert(err)
      }
    }

  return (
    <div>
        <h1>Make a Delivery</h1>
        <input type="text" placeholder='Correct delivery hash' ref={deliveryHash} /><br />
        <button onClick={acceptDelivery}>Make Delivery</button>
    </div>
  )
}

export default AcceptDelivery
