import React, {useRef, useState} from 'react'
import useEth from "../contexts/EthContext/useEth";
import Axios from 'axios'

const serverUrl = 'http://localhost:3000/deliveries/'
let userAddress = null;

const AcceptDelivery = () => {
  
    const { state: { contract, accounts } } = useEth()
    const [deliveryRequests, setDeliveryRequests] = useState([])
    if(accounts != null) {
      userAddress = accounts[0]
    }

    if(userAddress != null) {
      Axios.get(serverUrl+'stage/0').then(response => {
        setDeliveryRequests(response.data)
      })
    }

    const acceptDelivery = async (index) => {
      const hash = deliveryRequests[index]._id
      if(hash === '') {
        alert('There was a problem, please try again.');
        return;
      }
      try {
        await contract.methods.acceptDelivery(hash).send({from: accounts[0]}).then((response) => {
          console.log(response)
        })
      } catch(err) {
        alert(err)
      }
    }

  return (
    <div>
        <h1>Make a Delivery</h1>
        <table style={{border: "1px solid black"}}>
          <tr>
            <th style={{border: "1px solid black", padding: "10px"}}>Index</th>
            <th style={{border: "1px solid black", padding: "10px"}}>Item</th>
            <th style={{border: "1px solid black", padding: "10px"}}>Pickup From</th>
            <th style={{border: "1px solid black", padding: "10px"}}>Deliver To</th>
            <th style={{border: "1px solid black", padding: "10px"}}>Reward</th>
            <th style={{border: "1px solid black", padding: "10px"}}>Accept Delivery</th>
          </tr>
          {deliveryRequests != null ? deliveryRequests.map((req, index) =>  {
            return (
              <tr>
                <td style={{border: "1px solid black", padding: "10px"}}>{index+1}</td>
                <td style={{border: "1px solid black", padding: "3px"}}>{req.item}</td>
                <td style={{border: "1px solid black", padding: "3px"}}>{req.from}</td>
                <td style={{border: "1px solid black", padding: "3px"}}>{req.to}</td>
                <td style={{border: "1px solid black", padding: "3px"}}>{req.payment}</td>
                <td style={{border: "1px solid black", padding: "10px"}}><button onClick={() => acceptDelivery(index)}>Accept</button></td>
              </tr>
            )
          }): console.log("deliveryRequests was null.")}
        </table>
        {deliveryRequests != null ? deliveryRequests.length == 0 ? "No delivery requests yet." : null : null}
    </div>
  )
}

export default AcceptDelivery
