import React, {useState} from 'react'
import useEth from "../contexts/EthContext/useEth";

let listeningToAllegeEvent = false;
let listeningToAcceptEvent = false;

const RequestDelivery = () => {
  const { state: { contract, accounts } } = useEth();
  const [requests, setRequests] = useState([])
  if(accounts != null) {
    userAddress = accounts[0]
  }

  if(userAddress != null) {
    Axios.get(serverUrl+'address/' + userAddress).then(response => {
      setRequests(response.data)
    })
  }

  const [to, setTo] = useState('')
  const [from, setFrom] = useState('')
  const [item, setItem] = useState('')
  const [value, setValue] = useState(0)
  window.allegeEvent = null;


  // starting listener for Accept Event.
  if (contract != null && !listeningToAcceptEvent) {
    window.acceptEvent = contract.events.AcceptEvent()
    .on("data", function(log) {
      handleAccept(log);
    })
    listeningToAcceptEvent = true;
  }

  // starting listener for Allege Event.
  if (contract != null && !listeningToAllegeEvent) {
    window.allegeEvent = contract.events.AllegeEvent()
    .on("data", function(log) {
      handleAllege(log);
    })
    listeningToAllegeEvent = true;
  }

  const handleInput = e => {
    switch(e.target.name) {
        case 'to': 
            setTo(e.target.value);
            break;
        case 'from':
            setFrom(e.target.value);
            break;
        case 'item': 
            setItem(e.target.value);
            break;
        case 'value': 
            setValue(e.target.value);
            break;
        default: 
            alert('Invalid input source.')
            break;
    }
  }

  const handleSubmit = async () => {
    // Validation: 
    if (to === '' || from === '' || item === '' || value === 0) {
        alert("Please fill in all the values.")
        return;
    }

    // Calling Contract if validation succeeds: 
    await contract.methods.requestDelivery(to, from, item).send({from: accounts[0], value: value}).then((response) => {
      console.log(response)
      let tempArray = requests
      tempArray.push(response.events.RequestEvent.returnValues.requestId)
      setRequests(tempArray)
    })
  }

  window.handleConfirm = async (index) => {
    let requestId = requests[index]._id
    await contract.methods.confirm(requestId).send({from: accounts[0]})
  }

  const handleAccept = (log) => {
    let requestId = log.returnValues.requestId
    requests.forEach((request, index) => {
      if (request._id == requestId) {
        requests[index].status = 1
        requests[index].deliveryMan = log.returnValues.deliveryMan
        return;
      }
    }) 
  }

  const handleAllege = (log) => {
    let requestId = log.returnValues.requestId
    requests.forEach((request, index) => {
      if (request._id == requestId) {
        requests[index].status = 2
        return;
      }
    }) 
  }

  return (
    <div id='wrapper'>
      <div>
        <h1>Request a Delivery</h1>
        <label htmlFor="to">Delivery Destination: </label>
        <input type="text" name='to' onChange={handleInput}/>

        <label htmlFor="from">Pickup From: </label>
        <input type="text" name='from' onChange={handleInput}/>

        <label htmlFor="item">Pick this up: </label>
        <input type="text" name='item' onChange={handleInput} />

        <label htmlFor="value">Attach payment(in wei): </label>
        <input type="number" name='value' onChange={handleInput} />

        <button onClick={handleSubmit}>Submit</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Item</th>
            <th>From</th>
            <th>To</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {requests != null ? requests.map((request, index) => {
            return (
              <tr>
              <td>{request.item}</td>
              <td>{request.from}</td>
              <td>{request.to}</td>
              <td>{request.status = "0" ? "Requested" : request.status = "1" ? "On the way" : request.status = "2" ? <button onClick={handleConfirm(index)}>Confirm</button> : "Completed. "}</td>
            </tr>
            )
          }): "You have not requestsed anything yet."}
        </tbody>
      </table>
    </div>
  )
}

export default RequestDelivery
